-- =============================================
-- VERIFICATION QUERIES
-- =============================================

-- Count total mappings (should be 120 = 15 sections × 8 subjects)
SELECT 'Total Section-Subject Mappings' as metric, COUNT(*) as count FROM section_subjects;

-- Faculty load analysis - ALL faculty with both theory and lab
SELECT 
    f.code as faculty_code,
    f.name as faculty_name,
    COUNT(CASE WHEN s.subject_type = 'theory' THEN 1 END) as theory_count,
    COUNT(CASE WHEN s.subject_type = 'lab' THEN 1 END) as lab_count,
    SUM(s.periods_per_week) as total_periods,
    48 as available_slots,
    ROUND(SUM(s.periods_per_week)::numeric / 48 * 100, 0) as utilization_pct
FROM section_subjects ss
JOIN faculty f ON ss.faculty_id = f.id
JOIN subjects s ON ss.subject_id = s.id
GROUP BY f.id, f.code, f.name
ORDER BY f.code;

-- Summary of new distribution
SELECT '===========================================' as info;
SELECT 'OPTIMIZED FACULTY LOAD DISTRIBUTION:' as info;
SELECT '===========================================' as info;
SELECT 'F007: 1 theory × 8 sections = 32 periods (67%) - anchor only' as f007_load;
SELECT 'F001-F004: 1 theory × 8 sections + 4 labs = 48 periods (100%)' as f001_f004_load;
SELECT 'F005-F006: Lab backup only = ~16 periods (33%)' as f005_f006_load;
SELECT '' as spacer;
SELECT 'ALL FACULTY WITHIN CAPACITY - No fallback needed!' as solution;
